class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {term: ''}; // class based components
  }

  render() {
    return(

      <div>
        <input
          onChange={ event => this.setState({term: event.target.value})} />
        Value of the input: { this.state.term }
      </div>
    );
  }
}

